import React, { useRef, useState } from 'react'
import Header from './Header'
import {checkValidation} from './validation'
export const Login = () => {
  const [issignForm, setissignForm] = useState(true);
  const handleButton = () => {
    checkValidation(email,password);
  }
  const clickSign = () => {
    setissignForm(!issignForm);
    // console.log("welcome");
  };
  return (
    <div>
      <Header />
      <div className='absolute'>
        <img src="https://assets.nflxext.com/ffe/siteui/vlv3/41c789f0-7df5-4219-94c6-c66fe500590a/3149e5eb-4660-4e3d-9e65-b1e615229c64/IN-en-20240513-popsignuptwoweeks-perspective_alpha_website_small.jpg" />
      </div>
      <form className='w-3/12 mx-auto my-36 right-0 left-0 absolute bg-black p-12'>
        <h1 className='text-center text-white'>{issignForm ? "Sign In" : "Sign Up"}</h1>
        {!issignForm && (!issignForm?<input type="text" placeholder='Full Name' className='p-2 m-2 rounded-lg' />:'')}
        <input ref={useRef} type="text" placeholder='email address' className='p-2 m-2 rounded-lg' />
        <input ref={useRef} type="password" placeholder='password' className='p-2 m-2 rounded-lg' />
        <button className='p-4 m-2 text-white bg-red-600 rounded-lg' onClick={handleButton}>{issignForm ? "Sign In" : "Sign up"}</button>
        
        <p className='text-white' onClick={clickSign}>
          {issignForm?"need to netflix please sign up":"already register please sign in"}
          
          </p>
      </form>
    </div>

  );
};

export default Login;
