import React from "react";
import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { MENU_APIURL } from "./utils/constants";
import Restorentcategory from "./Restorentcategory";
const Restorentmenu = () => {
    const [restinfo, setResinfo] = useState(null);
    const { resId } = useParams();
   // console.log(resId);
    useEffect(() => {
       // console.log(`Use effect call`);
        fetchMenu();
    }, []);
    const fetchMenu = async () => {
        const data = await fetch(MENU_APIURL + resId)
       //  console.log(data);
        const json = await data.json();
     console.log("actual data", json);
        setResinfo(json.data); 
        const [name,cuisines] = restinfo.cards[2]?.card?.card?.info;
    };
   
   // const itemCards = restinfo?.cards[4]?.groupedCard?.cardGroupMap?.REGULAR?.cards[3]?.card?.card;
    const categories = restinfo?.cards[4]?.groupedCard?.cardGroupMap?.REGULAR?.cards.filter(
        (c) => c.card?.['card']?.['@type'] == "type.googleapis.com/swiggy.presentation.food.v2.ItemCategory"
    ) || [];
    //console.log(`====================>`)
    console.log(categories);
    return (
        <div className="text-center">
            {/* <h1>{restinfo?.cards[2]?.card?.card?.info?.name}</h1>
            <h1>{restinfo?.cards[2]?.card?.card?.info?.avgRating}-{restinfo?.cards[2]?.card?.card?.info?.costForTwoMessage}</h1>
            <h2>{restinfo?.cards[2]?.card?.card?.info?.cuisines.join(",")}</h2> */}
            {/* <h1>{name}</h1>
            <p>{cuisines}</p> */}
            
            {categories.map((item) =>
            
                <Restorentcategory key={item.card.card.title} data={item.card.card} />
            )}

        </div>
    )
}

export default Restorentmenu;
