import { useDispatch } from "react-redux";
import { addItem } from "./utils/cartslice";
import { cdn_img } from "./utils/constants";

const ItemsList = ({ items }) => {
    const dispatch = useDispatch();
    const addToCart =()=>{
        dispatch(addItem());
        //console.log("add to cart");
    }
    //console.log(items);
    return <div className="">
        <div className="border-gray-100 border-b-2">
            {items.map((item) => (
                <div key={item.card.info.id} className="p-2 m-2 border-b-2 ">
                    <div className="p-2 w-3/12 float-end">
                        <div className="absolute">
                            <button className="bg-black text-white p-2 m-auto" onClick={addToCart}>
                                Add +
                            </button>
                        </div>
                        <img className="w-24 text-right" src={cdn_img + item.card.info.imageId} />
                    </div>
                    <div className="w-9/12 p-4">
                        <div className="p-2 text-left font-bold">
                            <span className="p-2 m-2">{item.card.info.name}</span>
                            <span>{item.card.info.price / 100} RS</span>
                        </div>
                        <p className="text-xs">{item.card.info.description}</p>
                    </div>
                </div>

            ))}
        </div>
    </div>;
}
export default ItemsList;
